import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { getUserById, updateUser } from "../services/api";
import UserForm from "../components/UserForm";

/**
 * EditUserPage - fetches a user, pre-fills form, updates user on submit
 */
export default function EditUserPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [initial, setInitial] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch();
    // eslint-disable-next-line
  }, [id]);

  async function fetch() {
    setLoading(true);
    try {
      const u = await getUserById(id);
      const nameParts = (u.name || "").split(" ");
      const firstName = nameParts[0] || "";
      const lastName = nameParts.slice(1).join(" ") || "";
      const department = (u.company && u.company.name) ? u.company.name : "General";
      setInitial({ firstName, lastName, email: u.email || "", department });
    } catch (err) {
      alert("Failed to fetch user: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  const handleUpdate = async (formData) => {
    const payload = {
      name: `${formData.firstName} ${formData.lastName}`,
      email: formData.email,
      company: { name: formData.department || "General" },
    };
    try {
      await updateUser(id, payload); // simulated
      alert("User updated (simulated)");
      navigate("/");
    } catch (err) {
      alert("Failed to update user: " + err.message);
    }
  };

  if (loading) return <div className="alert alert-info">Loading user...</div>;
  if (!initial) return <div className="alert alert-warning">No user data</div>;

  return (
    <div className="card mx-auto" style={{ maxWidth: 720 }}>
      <div className="card-body">
        <h4 className="card-title">Edit User</h4>
        <UserForm initialData={initial} onSubmit={handleUpdate} submitLabel="Update User" />
      </div>
    </div>
  );
}
