import React, { useEffect, useState } from "react";
import { getUsers, deleteUser } from "../services/api";
import UserTable from "../components/UserTable";
import Pagination from "../components/Pagination";
import SearchBar from "../components/SearchBar";
import FilterPopup from "../components/FilterPopup";

const UsersPage = () => {
  const [users, setUsers] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filters, setFilters] = useState({});
  const [sortField, setSortField] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [pageLimit, setPageLimit] = useState(10);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const data = await getUsers();
      // Add department manually (mock API doesn’t provide it)
      const withDept = data.map((user, index) => ({
        ...user,
        department: ["IT", "HR", "Finance", "Marketing"][index % 4],
      }));
      setUsers(withDept);
    } catch (error) {
      alert("Failed to fetch users");
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteUser(id);
      setUsers(users.filter((u) => u.id !== id));
    } catch (error) {
      alert("Failed to delete user");
    }
  };

  const handleEdit = (user) => {
    alert(`Navigate to Edit Page for User ID: ${user.id}`);
    // Normally use React Router to navigate
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
    setCurrentPage(1);
  };

  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
    setCurrentPage(1);
  };

  const handleSort = (field) => {
    setSortField(field);
  };

  // Filtering logic
  const filteredUsers = users.filter((user) => {
    const [firstName, lastName] = user.name.split(" ");

    const matchSearch =
      !searchQuery ||
      user.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      user.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
      user.department.toLowerCase().includes(searchQuery.toLowerCase());

    const matchFirst =
      !filters.firstName ||
      firstName?.toLowerCase().includes(filters.firstName.toLowerCase());

    const matchLast =
      !filters.lastName ||
      lastName?.toLowerCase().includes(filters.lastName.toLowerCase());

    const matchEmail =
      !filters.email ||
      user.email.toLowerCase().includes(filters.email.toLowerCase());

    const matchDept =
      !filters.department ||
      user.department.toLowerCase() === filters.department.toLowerCase();

    return matchSearch && matchFirst && matchLast && matchEmail && matchDept;
  });

  // Sorting logic
  const sortedUsers = [...filteredUsers].sort((a, b) => {
    if (!sortField) return 0;
    if (sortField === "firstName") {
      return a.name.split(" ")[0].localeCompare(b.name.split(" ")[0]);
    }
    if (sortField === "lastName") {
      return (a.name.split(" ")[1] || "").localeCompare(
        b.name.split(" ")[1] || ""
      );
    }
    return a[sortField]?.toString().localeCompare(b[sortField]?.toString());
  });

  // Pagination logic
  const startIndex = (currentPage - 1) * pageLimit;
  const paginatedUsers = sortedUsers.slice(
    startIndex,
    startIndex + pageLimit
  );

  return (
    <div style={{ padding: "20px" }}>
      <h2>User Management Dashboard</h2>

      {/* Toolbar */}
      <div style={{ marginBottom: "20px", display: "flex", gap: "10px" }}>
        <SearchBar onSearch={handleSearch} />
        <FilterPopup onFilterChange={handleFilterChange} />
        <button onClick={() => alert("Navigate to Add User Page")}>
          + Add User
        </button>
      </div>

      {/* User Table */}
      <UserTable
        users={paginatedUsers}
        onEdit={handleEdit}
        onDelete={handleDelete}
        onSort={handleSort}
      />

      {/* Pagination */}
      <Pagination
        currentPage={currentPage}
        totalItems={sortedUsers.length}
        pageLimit={pageLimit}
        onPageChange={setCurrentPage}
        onLimitChange={setPageLimit}
      />
    </div>
  );
};

export default UsersPage;




