import React, { useState } from "react";

const FilterPopup = ({ onFilterChange }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [filters, setFilters] = useState({
    firstName: "",
    lastName: "",
    email: "",
    department: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const handleApply = () => {
    onFilterChange(filters);
    setIsOpen(false);
  };

  const handleClear = () => {
    const cleared = { firstName: "", lastName: "", email: "", department: "" };
    setFilters(cleared);
    onFilterChange(cleared);
    setIsOpen(false);
  };

  return (
    <div style={{ display: "inline-block", marginLeft: "10px" }}>
      <button onClick={() => setIsOpen(!isOpen)}>Filter</button>

      {isOpen && (
        <div
          style={{
            position: "absolute",
            marginTop: "5px",
            padding: "15px",
            border: "1px solid #ccc",
            borderRadius: "4px",
            backgroundColor: "#fff",
            zIndex: 1000,
            width: "250px",
          }}
        >
          <h4 style={{ marginTop: 0 }}>Filter Users</h4>

          {/* First Name */}
          <label style={{ display: "block", marginBottom: "5px" }}>
            First Name:
          </label>
          <input
            type="text"
            name="firstName"
            value={filters.firstName}
            onChange={handleChange}
            style={{ width: "100%", marginBottom: "10px" }}
          />

          {/* Last Name */}
          <label style={{ display: "block", marginBottom: "5px" }}>
            Last Name:
          </label>
          <input
            type="text"
            name="lastName"
            value={filters.lastName}
            onChange={handleChange}
            style={{ width: "100%", marginBottom: "10px" }}
          />

          {/* Email */}
          <label style={{ display: "block", marginBottom: "5px" }}>
            Email:
          </label>
          <input
            type="text"
            name="email"
            value={filters.email}
            onChange={handleChange}
            style={{ width: "100%", marginBottom: "10px" }}
          />

          {/* Department */}
          <label style={{ display: "block", marginBottom: "5px" }}>
            Department:
          </label>
          <select
            name="department"
            value={filters.department}
            onChange={handleChange}
            style={{ width: "100%", marginBottom: "10px" }}
          >
            <option value="">-- Select Department --</option>
            <option value="IT">IT</option>
            <option value="HR">HR</option>
            <option value="Finance">Finance</option>
            <option value="Marketing">Marketing</option>
          </select>

          {/* Buttons */}
          <div style={{ marginTop: "10px", textAlign: "right" }}>
            <button onClick={handleApply}>Apply</button>
            <button onClick={handleClear} style={{ marginLeft: "8px" }}>
              Clear
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default FilterPopup;





