import React, { useEffect, useState, useMemo } from "react";
import { getUsers, deleteUser } from "../services/api";
import UserTable from "../components/UserTable";
import SearchBar from "../components/SearchBar";
import Pagination from "../components/Pagination";
import FilterPopup from "../components/FilterPopup";

/**
 * Utility: map JSONPlaceholder user to our model
 */
function mapUser(u) {
  const nameParts = (u.name || "").split(" ");
  const firstName = nameParts[0] || "";
  const lastName = nameParts.slice(1).join(" ") || "";
  const department = (u.company && u.company.name) ? u.company.name : "General";
  return {
    id: u.id,
    firstName,
    lastName,
    email: u.email || "",
    department,
    // keep original raw for edits if needed
    raw: u,
  };
}

export default function UsersPage() {
  const [rawUsers, setRawUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // UI states
  const [search, setSearch] = useState("");
  const [filters, setFilters] = useState({ firstName: "", lastName: "", email: "", department: "" });
  const [filterPopupOpen, setFilterPopupOpen] = useState(false);
  const [pageSize, setPageSize] = useState(10);
  const [page, setPage] = useState(1);
  const [sortConfig, setSortConfig] = useState({ key: "id", direction: "asc" });

  useEffect(() => {
    fetchUsers();
  }, []);

  // Fetch users from API
  async function fetchUsers() {
    setLoading(true);
    setError(null);
    try {
      const data = await getUsers();
      // map to our simpler model
      setRawUsers(data.map(mapUser));
    } catch (err) {
      console.error(err);
      setError(err.message || "Error fetching users");
    } finally {
      setLoading(false);
    }
  }

  // Delete user handler
  async function handleDelete(id) {
    try {
      await deleteUser(id); // simulated
      // remove locally to reflect UI changes
      setRawUsers((prev) => prev.filter((u) => u.id !== id));
      alert("User deleted (simulated)"); // simple notification
    } catch (err) {
      alert("Failed to delete user: " + err.message);
    }
  }

  // Sort handler (toggle)
  function handleSort(key) {
    setSortConfig((prev) => {
      if (prev.key === key) {
        return { key, direction: prev.direction === "asc" ? "desc" : "asc" };
      } else {
        return { key, direction: "asc" };
      }
    });
  }

  // Apply filters + search + sort
  const processed = useMemo(() => {
    // start with rawUsers
    let items = [...rawUsers];

    // filter popup fields (exact/contains)
    if (filters.firstName) {
      items = items.filter((u) => u.firstName.toLowerCase().includes(filters.firstName.toLowerCase()));
    }
    if (filters.lastName) {
      items = items.filter((u) => u.lastName.toLowerCase().includes(filters.lastName.toLowerCase()));
    }
    if (filters.email) {
      items = items.filter((u) => u.email.toLowerCase().includes(filters.email.toLowerCase()));
    }
    if (filters.department) {
      items = items.filter((u) => u.department.toLowerCase().includes(filters.department.toLowerCase()));
    }

    // global search across fields
    if (search && search.trim() !== "") {
      const q = search.toLowerCase();
      items = items.filter((u) =>
        u.firstName.toLowerCase().includes(q) ||
        u.lastName.toLowerCase().includes(q) ||
        u.email.toLowerCase().includes(q) ||
        u.department.toLowerCase().includes(q)
      );
    }

    // sort
    const { key, direction } = sortConfig || {};
    if (key) {
      items.sort((a, b) => {
        const va = (a[key] || "").toString().toLowerCase();
        const vb = (b[key] || "").toString().toLowerCase();
        if (va < vb) return direction === "asc" ? -1 : 1;
        if (va > vb) return direction === "asc" ? 1 : -1;
        return 0;
      });
    }

    return items;
  }, [rawUsers, search, filters, sortConfig]);

  // pagination calculations
  const totalItems = processed.length;
  const totalPages = Math.max(1, Math.ceil(totalItems / pageSize));
  // adjust page if current page out of range
  useEffect(() => {
    if (page > totalPages) setPage(totalPages);
  }, [totalPages, page]);

  const currentData = processed.slice((page - 1) * pageSize, page * pageSize);

  return (
    <div>
      <div className="d-flex flex-column flex-md-row align-items-start gap-3 justify-content-between mb-3">
        <div style={{ flex: 1, minWidth: 280 }}>
          <SearchBar value={search} onChange={(v) => { setSearch(v); setPage(1); }} />
        </div>

        <div className="d-flex gap-2 align-items-center">
          <div>
            <select className="form-select" value={pageSize} onChange={(e) => { setPageSize(Number(e.target.value)); setPage(1); }}>
              {[10, 25, 50, 100].map((n) => <option key={n} value={n}>{n} / page</option>)}
            </select>
          </div>

          <button className="btn btn-outline-secondary" onClick={() => setFilterPopupOpen(true)}>Filter</button>
          <button className="btn btn-outline-success" onClick={fetchUsers}>Refresh</button>
        </div>
      </div>

      {loading && <div className="alert alert-info">Loading users...</div>}
      {error && <div className="alert alert-danger">{error}</div>}

      <UserTable users={currentData} onDelete={handleDelete} onSort={handleSort} sortConfig={sortConfig} />

      <div className="d-flex justify-content-between align-items-center">
        <div><small>Showing {(page - 1) * pageSize + 1} - {Math.min(totalItems, page * pageSize)} of {totalItems} users</small></div>
        <Pagination currentPage={page} totalPages={totalPages} onPageChange={(p) => setPage(p)} />
      </div>

      <FilterPopup visible={filterPopupOpen} onClose={() => setFilterPopupOpen(false)} onApply={(f) => { setFilters(f); setPage(1); }} initialFilters={filters} />
    </div>
  );
}
