import React, { useEffect, useState } from "react";
import UserForm from "../components/UserForm";
import { updateUser, getUsers } from "../services/api";
import { useParams, useNavigate } from "react-router-dom";

const EditUserPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const users = await getUsers();
        const u = users.find((u) => u.id === Number(id));
        const [firstName, lastName] = u.name.split(" ");
        setUser({ firstName, lastName, email: u.email, department: u.department || "IT" });
      } catch {
        alert("Error fetching user");
      }
    };
    fetchUser();
  }, [id]);

  const handleSubmit = async (data) => {
    try {
      await updateUser(id, { name: `${data.firstName} ${data.lastName}`, email: data.email, department: data.department });
      alert("User updated successfully");
      navigate("/users");
    } catch {
      alert("Error updating user");
    }
  };

  return user ? (
    <div className="card p-4 shadow-sm">
      <h4>Edit User</h4>
      <UserForm initialData={user} onSubmit={handleSubmit} />
    </div>
  ) : (
    <p>Loading...</p>
  );
};

export default EditUserPage;
