import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { getUserById, updateUser } from "../services/api";

const EditUserPage = () => {
  const { id } = useParams();
  const [form, setForm] = useState({ name: "", email: "", department: "" });
  const navigate = useNavigate();

  useEffect(() => { fetchUser(); }, []);

  const fetchUser = async () => {
    try {
      const user = await getUserById(id);
      setForm({ name: user.name, email: user.email, department: user.department || "IT" });
    } catch { alert("Failed to fetch user"); }
  };

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateUser(id, form);
      navigate("/", { state: { updated: true } }); // navigate back and trigger refetch
    } catch { alert("Failed to update user"); }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "500px", margin: "auto" }}>
      <h2>Edit User</h2>
      <form style={{ display: "flex", flexDirection: "column", gap: "15px" }} onSubmit={handleSubmit}>
        <input type="text" placeholder="Name" name="name" value={form.name} onChange={handleChange} required />
        <input type="email" placeholder="Email" name="email" value={form.email} onChange={handleChange} required />
        <select name="department" value={form.department} onChange={handleChange}>
          <option value="IT">IT</option>
          <option value="HR">HR</option>
          <option value="Finance">Finance</option>
          <option value="Marketing">Marketing</option>
        </select>
        <button type="submit">Update User</button>
      </form>
    </div>
  );
};

export default EditUserPage;


