import React, { useEffect, useState } from "react";
import { getUsers, deleteUser } from "../services/api";
import UserTable from "../components/UserTable";
import Pagination from "../components/Pagination";
import SearchBar from "../components/SearchBar";
import FilterPopup from "../components/FilterPopup";
import { Button, Toast, ToastContainer } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const UsersPage = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageLimit, setPageLimit] = useState(10);
  const [searchQuery, setSearchQuery] = useState("");
  const [showFilter, setShowFilter] = useState(false);
  const [toastMsg, setToastMsg] = useState("");
  const [sortField, setSortField] = useState("id");
  const [sortOrder, setSortOrder] = useState("asc");
  const navigate = useNavigate();

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const data = await getUsers();
      const deptData = data.map((u) => ({ ...u, department: ["IT", "HR", "Sales"][u.id % 3] }));
      setUsers(deptData);
      setFilteredUsers(deptData);
    } catch (error) {
      showToast("Error fetching users");
    }
  };

  const showToast = (msg) => {
    setToastMsg(msg);
    setTimeout(() => setToastMsg(""), 3000);
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure to delete?")) {
      try {
        await deleteUser(id);
        const newList = users.filter((u) => u.id !== id);
        setUsers(newList);
        setFilteredUsers(newList);
        showToast("User deleted successfully");
      } catch {
        showToast("Error deleting user");
      }
    }
  };

  const handleFilter = (filterData) => {
    let filtered = users.filter((u) => {
      const [firstName, lastName] = u.name.split(" ");
      return (
        u.email.toLowerCase().includes(filterData.email.toLowerCase()) &&
        u.department.toLowerCase().includes(filterData.department.toLowerCase()) &&
        firstName.toLowerCase().includes(filterData.firstName.toLowerCase()) &&
        (lastName || "").toLowerCase().includes(filterData.lastName.toLowerCase())
      );
    });
    setFilteredUsers(filtered);
  };

  const handleSort = (field) => {
    const order = sortField === field && sortOrder === "asc" ? "desc" : "asc";
    const sorted = [...filteredUsers].sort((a, b) => {
      let valA, valB;
      if (field === "firstName") {
        valA = a.name.split(" ")[0].toLowerCase();
        valB = b.name.split(" ")[0].toLowerCase();
      } else if (field === "lastName") {
        valA = (a.name.split(" ")[1] || "").toLowerCase();
        valB = (b.name.split(" ")[1] || "").toLowerCase();
      } else {
        valA = (a[field] || "").toString().toLowerCase();
        valB = (b[field] || "").toString().toLowerCase();
      }
      if (valA < valB) return order === "asc" ? -1 : 1;
      if (valA > valB) return order === "asc" ? 1 : -1;
      return 0;
    });
    setSortField(field);
    setSortOrder(order);
    setFilteredUsers(sorted);
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
    const filtered = users.filter((u) => {
      const [firstName, lastName] = u.name.split(" ");
      return (
        firstName.toLowerCase().includes(query.toLowerCase()) ||
        (lastName || "").toLowerCase().includes(query.toLowerCase()) ||
        u.email.toLowerCase().includes(query.toLowerCase()) ||
        u.department.toLowerCase().includes(query.toLowerCase())
      );
    });
    setFilteredUsers(filtered);
  };

  const paginatedUsers = filteredUsers.slice((currentPage - 1) * pageLimit, currentPage * pageLimit);

  return (
    <div>
      <div className="d-flex justify-content-between mb-2">
        <Button variant="success" onClick={() => navigate("/add")}>
          Add User
        </Button>
        <div>
          <Button variant="info" onClick={() => setShowFilter(true)} className="me-2">
            Filter
          </Button>
          <select value={pageLimit} onChange={(e) => setPageLimit(Number(e.target.value))} className="form-select d-inline-block w-auto">
            {[10, 25, 50, 100].map((n) => (
              <option key={n} value={n}>
                {n}
              </option>
            ))}
          </select>
        </div>
      </div>
      <SearchBar searchQuery={searchQuery} setSearchQuery={handleSearch} />
      <UserTable users={paginatedUsers} onDelete={handleDelete} onSort={handleSort} sortField={sortField} sortOrder={sortOrder} />
      <Pagination total={filteredUsers.length} limit={pageLimit} currentPage={currentPage} setCurrentPage={setCurrentPage} />
      <FilterPopup show={showFilter} handleClose={() => setShowFilter(false)} applyFilter={handleFilter} />
      <ToastContainer position="top-end" className="p-3">
        {toastMsg && <Toast onClose={() => setToastMsg("")} show={!!toastMsg} delay={2000} autohide>{toastMsg}</Toast>}
      </ToastContainer>
    </div>
  );
};

