import React from "react";
import { Link } from "react-router-dom";

/**
 * UserTable component
 * Props:
 *  - users: array of user objects { id, firstName, lastName, email, department }
 *  - onDelete(userId)
 *  - onSort(column)
 *  - sortConfig { key, direction }
 */
export default function UserTable({ users, onDelete, onSort, sortConfig }) {
  const makeSortHandler = (key) => () => onSort(key);

  const getSortIcon = (key) => {
    if (!sortConfig || sortConfig.key !== key) return null;
    return sortConfig.direction === "asc" ? " ▲" : " ▼";
  };

  return (
    <div className="table-responsive">
      <table className="table table-hover align-middle">
        <thead className="table-light">
          <tr>
            <th style={{ width: 60 }}>ID</th>
            <th style={{ cursor: "pointer" }} onClick={makeSortHandler("firstName")}>First Name{getSortIcon("firstName")}</th>
            <th style={{ cursor: "pointer" }} onClick={makeSortHandler("lastName")}>Last Name{getSortIcon("lastName")}</th>
            <th style={{ cursor: "pointer" }} onClick={makeSortHandler("email")}>Email{getSortIcon("email")}</th>
            <th style={{ cursor: "pointer" }} onClick={makeSortHandler("department")}>Department{getSortIcon("department")}</th>
            <th style={{ width: 170 }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.length === 0 && (
            <tr>
              <td colSpan={6} className="text-center py-4">No users found</td>
            </tr>
          )}
          {users.map((u) => (
            <tr key={u.id}>
              <td>{u.id}</td>
              <td>{u.firstName}</td>
              <td>{u.lastName}</td>
              <td className="text-truncate" style={{ maxWidth: 220 }}>{u.email}</td>
              <td>{u.department}</td>
              <td>
                <Link to={`/edit/${u.id}`} className="btn btn-sm btn-outline-primary me-2">Edit</Link>
                <button className="btn btn-sm btn-outline-danger" onClick={() => {
                  if (window.confirm(`Delete ${u.firstName} ${u.lastName}?`)) onDelete(u.id);
                }}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
