import React, { useEffect, useState } from "react";
import UserTable from "../components/UserTable";
import FilterPopup from "../components/FilterPopup";
import { getUsers, deleteUser } from "../services/api";
import { useNavigate } from "react-router-dom";

const UsersPage = () => {
  const [users, setUsers] = useState([]);
  const [filter, setFilter] = useState(null);
  const [sortBy, setSortBy] = useState("id"); // default sort
  const navigate = useNavigate();

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    const data = await getUsers();
    setUsers(data);
  };

  const handleDelete = async (id) => {
    await deleteUser(id);
    setUsers(users.filter((user) => user.id !== id));
  };

  const handleFilterChange = (filterValue) => {
    setFilter(filterValue);
  };

  const handleSortChange = (e) => {
    setSortBy(e.target.value);
  };

  // filtering
  let filteredUsers = [...users];
  if (filter) {
    filteredUsers = filteredUsers.filter(
      (user) => user.department.toLowerCase() === filter.toLowerCase()
    );
  }

  // sorting
  let sortedUsers = [...filteredUsers];
  if (sortBy === "id") {
    sortedUsers.sort((a, b) => a.id - b.id);
  } else if (sortBy === "firstName") {
    sortedUsers.sort((a, b) => a.firstName.localeCompare(b.firstName));
  } else if (sortBy === "department") {
    sortedUsers.sort((a, b) => a.department.localeCompare(b.department));
  }

  return (
    <div style={{ padding: "20px" }}>
      <h2>User Management Dashboard</h2>
      <div style={{ marginBottom: "10px" }}>
        <button onClick={() => navigate("/add")}>+ Add User</button>
        <FilterPopup onFilterChange={handleFilterChange} />

        {/* Sort Dropdown */}
        <select value={sortBy} onChange={handleSortChange} style={{ marginLeft: "10px" }}>
          <option value="id">Sort by ID</option>
          <option value="firstName">Sort by First Name</option>
          <option value="department">Sort by Department</option>
        </select>
      </div>

      <UserTable users={sortedUsers} onDelete={handleDelete} />
    </div>
  );
};

export default UsersPage;



