import React, { useState, useEffect } from "react";
import { validateUser } from "../utils/validation";

/**
 * UserForm component used for Add and Edit
 * Props:
 *  - initialData { firstName, lastName, email, department }
 *  - onSubmit(data) -> async or sync
 */
export default function UserForm({ initialData = {}, onSubmit, submitLabel = "Save" }) {
  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    email: "",
    department: "",
    ...initialData,
  });
  const [errors, setErrors] = useState({});
  const [submitting, setSubmitting] = useState(false);
  useEffect(() => setForm({ firstName: "", lastName: "", email: "", department: "", ...initialData }), [initialData]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { valid, errors: vErrors } = validateUser(form);
    setErrors(vErrors);
    if (!valid) return;
    try {
      setSubmitting(true);
      await onSubmit(form);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} noValidate>
      <div className="mb-3">
        <label className="form-label">First name *</label>
        <input className={`form-control ${errors.firstName ? "is-invalid" : ""}`} value={form.firstName} onChange={(e) => setForm({ ...form, firstName: e.target.value })} />
        <div className="invalid-feedback">{errors.firstName}</div>
      </div>

      <div className="mb-3">
        <label className="form-label">Last name *</label>
        <input className={`form-control ${errors.lastName ? "is-invalid" : ""}`} value={form.lastName} onChange={(e) => setForm({ ...form, lastName: e.target.value })} />
        <div className="invalid-feedback">{errors.lastName}</div>
      </div>

      <div className="mb-3">
        <label className="form-label">Email *</label>
        <input className={`form-control ${errors.email ? "is-invalid" : ""}`} value={form.email} onChange={(e) => setForm({ ...form, email: e.target.value })} />
        <div className="invalid-feedback">{errors.email}</div>
      </div>

      <div className="mb-3">
        <label className="form-label">Department</label>
        <input className="form-control" value={form.department} onChange={(e) => setForm({ ...form, department: e.target.value })} />
      </div>

      <div className="d-flex justify-content-end">
        <button className="btn btn-primary" type="submit" disabled={submitting}>
          {submitting ? "Saving..." : submitLabel}
        </button>
      </div>
    </form>
  );
}
