import React, { useState, useEffect } from "react";
import { Container, Row, Col, Button } from "react-bootstrap";
import UserTable from "../components/UserTable";
import { getUsers, deleteUser } from "../services/api";
import { useNavigate } from "react-router-dom";
import FilterPopup from "../components/FilterPopup";
import Pagination from "../components/Pagination";

const UsersPage = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [showFilter, setShowFilter] = useState(false);
  const [sortConfig, setSortConfig] = useState({ key: "id", direction: "asc" });

  // Pagination
  const [currentPage, setCurrentPage] = useState(1);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  const navigate = useNavigate();

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const data = await getUsers();

      // Split name into firstName & lastName + enrich with department
      const enrichedData = data.map((user) => {
        const [firstName, ...rest] = user.name.split(" ");
        const lastName = rest.join(" ") || "";
        return {
          ...user,
          firstName,
          lastName,
          department: user.company?.name || "N/A",
        };
      });

      setUsers(enrichedData);
      setFilteredUsers(enrichedData);
    } catch {
      alert("Error fetching users");
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure to delete this user?")) {
      try {
        await deleteUser(id);
        const updatedUsers = users.filter((u) => u.id !== id);
        setUsers(updatedUsers);
        setFilteredUsers(updatedUsers);
      } catch {
        alert("Error deleting user");
      }
    }
  };

  // 🔍 Search
  const handleSearch = (query) => {
    setSearchQuery(query);
    const filtered = users.filter((user) => {
      return (
        user.firstName.toLowerCase().includes(query.toLowerCase()) ||
        user.lastName.toLowerCase().includes(query.toLowerCase()) ||
        user.email.toLowerCase().includes(query.toLowerCase()) ||
        user.department.toLowerCase().includes(query.toLowerCase())
      );
    });
    setFilteredUsers(filtered);
  };

  // 🎯 Filter
  const handleFilter = (filters) => {
    const filtered = users.filter((user) => {
      return (
        (!filters.firstName || user.firstName.toLowerCase().includes(filters.firstName.toLowerCase())) &&
        (!filters.lastName || user.lastName.toLowerCase().includes(filters.lastName.toLowerCase())) &&
        (!filters.email || user.email.toLowerCase().includes(filters.email.toLowerCase())) &&
        (!filters.department || user.department.toLowerCase().includes(filters.department.toLowerCase()))
      );
    });
    setFilteredUsers(filtered || []);
    setShowFilter(false);
  };

  const handleClearFilter = () => {
    setFilteredUsers(users);
  };

  // 🔽 Sorting fix
  const handleSort = (key) => {
    let direction = "asc";
    if (sortConfig.key === key && sortConfig.direction === "asc") direction = "desc";
    setSortConfig({ key, direction });
  };

  const sortedUsers = [...filteredUsers].sort((a, b) => {
    const { key, direction } = sortConfig;
    let compareA = a[key];
    let compareB = b[key];

    if (key === "id") {
      compareA = Number(compareA);
      compareB = Number(compareB);
    } else {
      compareA = (compareA || "").toString().toLowerCase();
      compareB = (compareB || "").toString().toLowerCase();
    }

    if (compareA < compareB) return direction === "asc" ? -1 : 1;
    if (compareA > compareB) return direction === "asc" ? 1 : -1;
    return 0;
  });

  // 📄 Pagination logic
  const indexOfLastUser = currentPage * rowsPerPage;
  const indexOfFirstUser = indexOfLastUser - rowsPerPage;
  const currentUsers = sortedUsers.slice(indexOfFirstUser, indexOfLastUser);

  return (
    <Container className="mt-4">
      <Row className="mb-3">
        <Col className="text-end">
          <Button variant="primary" onClick={() => navigate("/add")}>
            Add User
          </Button>{" "}
          <Button variant="secondary" onClick={() => setShowFilter(!showFilter)}>
            Filter
          </Button>{" "}
          <Button variant="outline-secondary" onClick={handleClearFilter}>
            Clear Filter
          </Button>
        </Col>
      </Row>

      {showFilter && <FilterPopup onApply={handleFilter} onClose={() => setShowFilter(false)} />}

      {currentUsers.length > 0 ? (
        <UserTable
          users={currentUsers}
          onDelete={handleDelete}
          onEdit={(id) => navigate(`/edit/${id}`)}
          onSearch={handleSearch}
          onSort={handleSort}
          sortConfig={sortConfig}
        />
      ) : (
        <p style={{ color: "red", textAlign: "center" }}>No users found</p>
      )}

      {/* Pagination Controls */}
      <Pagination
        totalUsers={sortedUsers.length}
        rowsPerPage={rowsPerPage}
        setRowsPerPage={setRowsPerPage}
        currentPage={currentPage}
        setCurrentPage={setCurrentPage}
      />
    </Container>
  );
};

export default UsersPage;



