import React from "react";

/**
 * Simple search bar component
 * Props:
 *  - value
 *  - onChange (newValue) 
 */
export default function SearchBar({ value, onChange, placeholder = "Search by name / email / department" }) {
  return (
    <div className="mb-3">
      <input
        type="search"
        className="form-control"
        placeholder={placeholder}
        value={value}
        onChange={(e) => onChange(e.target.value)}
      />
    </div>
  );
}
