import React from "react";

const Pagination = ({ currentPage, totalItems, pageLimit, onPageChange, onLimitChange }) => {
  const totalPages = Math.ceil(totalItems / pageLimit);
  const pages = [];
  for (let i = 1; i <= totalPages; i++) pages.push(i);

  return (
    <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginTop: "20px", flexWrap: "wrap", gap: "10px" }}>
      <div>
        <label>Rows per page: </label>
        <select value={pageLimit} onChange={(e) => onLimitChange(Number(e.target.value))}>
          {[10, 25, 50, 100].map((n) => (
            <option key={n} value={n}>{n}</option>
          ))}
        </select>
      </div>

      <div style={{ display: "flex", gap: "5px", flexWrap: "wrap" }}>
        {pages.map((num) => (
          <button
            key={num}
            onClick={() => onPageChange(num)}
            style={{
              padding: "5px 10px",
              backgroundColor: num === currentPage ? "#007bff" : "#f0f0f0",
              color: num === currentPage ? "#fff" : "#000",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            {num}
          </button>
        ))}
      </div>
    </div>
  );
};

export default Pagination;



