import React, { useEffect, useState } from "react";
import { getUsers, deleteUser } from "../services/api";
import UserTable from "../components/UserTable";
import Pagination from "../components/Pagination";
import SearchBar from "../components/SearchBar";
import FilterPopup from "../components/FilterPopup";
import { useNavigate, useLocation } from "react-router-dom";

const UsersPage = () => {
  const [users, setUsers] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filters, setFilters] = useState({});
  const [sortField, setSortField] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");
  const [currentPage, setCurrentPage] = useState(1);
  const [pageLimit, setPageLimit] = useState(10);
  const navigate = useNavigate();
  const location = useLocation(); // track route changes

  // Fetch users whenever location changes (e.g., after add/edit)
  useEffect(() => {
    fetchUsers();
  }, [location]);

  const fetchUsers = async () => {
    try {
      const data = await getUsers();
      const withDept = data.map((user, index) => ({
        ...user,
        department: ["IT", "HR", "Finance", "Marketing"][index % 4],
      }));
      setUsers(withDept);
    } catch (error) {
      alert("Failed to fetch users");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this user?")) return;
    try {
      await deleteUser(id);
      setUsers(users.filter((u) => u.id !== id));
    } catch (error) {
      alert("Failed to delete user");
    }
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
    setCurrentPage(1);
  };

  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
    setCurrentPage(1);
  };

  const handleSort = (field) => {
    if (sortField === field) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortField(field);
      setSortOrder("asc");
    }
  };

  // Filtering
  const filteredUsers = users.filter((user) => {
    const [firstName, lastName] = user.name.split(" ");
    const matchSearch =
      !searchQuery ||
      user.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      user.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
      user.department.toLowerCase().includes(searchQuery.toLowerCase());

    const matchFirst =
      !filters.firstName ||
      firstName?.toLowerCase().includes(filters.firstName.toLowerCase());
    const matchLast =
      !filters.lastName ||
      lastName?.toLowerCase().includes(filters.lastName.toLowerCase());
    const matchEmail =
      !filters.email || user.email.toLowerCase().includes(filters.email.toLowerCase());
    const matchDept =
      !filters.department || user.department.toLowerCase() === filters.department.toLowerCase();

    return matchSearch && matchFirst && matchLast && matchEmail && matchDept;
  });

  // Sorting
  const sortedUsers = [...filteredUsers].sort((a, b) => {
    if (!sortField) return 0;
    let valA, valB;
    switch (sortField) {
      case "firstName":
        valA = a.name.split(" ")[0];
        valB = b.name.split(" ")[0];
        break;
      case "lastName":
        valA = a.name.split(" ")[1] || "";
        valB = b.name.split(" ")[1] || "";
        break;
      default:
        valA = a[sortField];
        valB = b[sortField];
    }
    if (typeof valA === "string") valA = valA.toLowerCase();
    if (typeof valB === "string") valB = valB.toLowerCase();

    if (valA < valB) return sortOrder === "asc" ? -1 : 1;
    if (valA > valB) return sortOrder === "asc" ? 1 : -1;
    return 0;
  });

  // Pagination
  const startIndex = (currentPage - 1) * pageLimit;
  const paginatedUsers = sortedUsers.slice(startIndex, startIndex + pageLimit);

  return (
    <div style={{ padding: "20px", maxWidth: "1200px", margin: "auto" }}>
      <h2 style={{ textAlign: "center" }}>User Management Dashboard</h2>

      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          margin: "20px 0",
          flexWrap: "wrap",
          gap: "10px",
        }}
      >
        <SearchBar onSearch={handleSearch} />
        <FilterPopup onFilterChange={handleFilterChange} />
        <button onClick={() => navigate("/add")}>+ Add User</button>
      </div>

      <UserTable users={paginatedUsers} onDelete={handleDelete} onSort={handleSort} />

      <Pagination
        currentPage={currentPage}
        totalItems={sortedUsers.length}
        pageLimit={pageLimit}
        onPageChange={setCurrentPage}
        onLimitChange={setPageLimit}
      />
    </div>
  );
};

export default UsersPage;



