import React from "react";
import { useNavigate } from "react-router-dom";
import { addUser } from "../services/api";
import UserForm from "../components/UserForm";

/**
 * AddUserPage - shows form, calls API and navigates back on success
 */
export default function AddUserPage() {
  const navigate = useNavigate();

  const handleAdd = async (formData) => {
    // JSONPlaceholder expects fields like name, email, company etc.
    const payload = {
      name: `${formData.firstName} ${formData.lastName}`,
      email: formData.email,
      company: { name: formData.department || "General" },
    };
    try {
      const created = await addUser(payload);
      // Since JSONPlaceholder doesn't persist, it returns a simulated object with an id.
      alert("User added (simulated). ID: " + (created.id || "N/A"));
      navigate("/");
    } catch (err) {
      alert("Failed to add user: " + err.message);
    }
  };

  return (
    <div className="card mx-auto" style={{ maxWidth: 720 }}>
      <div className="card-body">
        <h4 className="card-title">Add New User</h4>
        <UserForm initialData={{}} onSubmit={handleAdd} submitLabel="Add User" />
      </div>
    </div>
  );
}
